<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" />        <title>North Texas Food Bank</title>        <link href="css/styles.css" rel="stylesheet" type="text/css" /><link rel="stylesheet" href="Tiny_style.css"/></head><body>            <div class="wrapper">            <div id="chart1" ></div>	<div id="tablewrapper">		<div id="tableheader">        	<div class="search">                <select id="columns" onchange="sorter.search('query')"></select>                <input type="text" id="query" onkeyup="sorter.search('query')" />            </div>            <span class="details">				<div>Records <span id="startrecord"></span>-<span id="endrecord"></span> of <span id="totalrecords"></span></div>        		<div><a href="javascript:sorter.reset()">reset</a></div>        	</span>        </div>        <table cellpadding="0" cellspacing="0" border="0" id="table" class="tinytable">            <thead>                <tr>                    <th class="nosort"><h3>Agent Id</h3></th>                    <th><h3>Agent Name</h3></th>                    <th><h3>Category Id</h3></th>                    <th><h3>Category Name</h3></th>                    <th><h3>Product Id</h3></th>                    <th><h3>Product Name</h3></th>                    <th><h3>Quantity</h3></th>                </tr>            </thead>            <tbody>                <tr>                    <td>1</td>                    <td>Dallas</td>                    <td>1</td>                    <td>Grain</td>                    <td>1</td>                    <td>Rice</td>                    <td>700</td>                </tr>                <tr>                    <td>1</td>                    <td>Dallas</td>                    <td>1</td>                    <td>Grain</td>                    <td>2</td>                    <td>Spaghetti</td>                    <td>400</td>                </tr>                <tr>                    <td>1</td>                    <td>Dallas</td>                    <td>1</td>                    <td>Protein</td>                    <td>1</td>                    <td>Ground Beef</td>                    <td>500</td>                </tr>            </tbody>        </table>        <div id="tablefooter">          <div id="tablenav">            	<div>                    <img src="images/first.gif" width="16" height="16" alt="First Page" onclick="sorter.move(-1,true)" />                    <img src="images/previous.gif" width="16" height="16" alt="First Page" onclick="sorter.move(-1)" />                    <img src="images/next.gif" width="16" height="16" alt="First Page" onclick="sorter.move(1)" />                    <img src="images/last.gif" width="16" height="16" alt="Last Page" onclick="sorter.move(1,true)" />                </div>                <div>                	<select id="pagedropdown"></select>				</div>                <div>                	<a href="javascript:sorter.showall()">view all</a>                </div>            </div>			<div id="tablelocation">            	<div>                    <select onchange="sorter.size(this.value)">                    <option value="5">5</option>                        <option value="10" selected="selected">10</option>                        <option value="20">20</option>                        <option value="50">50</option>                        <option value="100">100</option>                    </select>                    <span>Entries Per Page</span>                </div>                <div class="page">Page <span id="currentpage"></span> of <span id="totalpages"></span></div>            </div>        </div>    </div>	<script type="text/javascript" src="script.js"></script>	<script type="text/javascript">	var sorter = new TINY.table.sorter('sorter','table',{		headclass:'head',		ascclass:'asc',		descclass:'desc',		evenclass:'evenrow',		oddclass:'oddrow',		evenselclass:'evenselected',		oddselclass:'oddselected',		paginate:true,		size:10,		colddid:'columns',		currentid:'currentpage',		totalid:'totalpages',		startingrecid:'startrecord',		endingrecid:'endrecord',		totalrecid:'totalrecords',		hoverid:'selectedrow',		pageddid:'pagedropdown',		navid:'tablenav',		sortcolumn:1,		sortdir:1,		sum:[8],		avg:[6,7,8,9],		columns:[{index:7, format:'%', decimals:1},{index:8, format:'$', decimals:0}],		init:true	});  </script></body></html>